const App: React.FC = () => {
  const [names, setNames] = useState<string[]>(JSON.parse(_names));

  const groupNamesByLength = (): NameGroup[] => {
    const groups: { [length: number]: string[] } = {};

    names.forEach((name) => {
      const length = name.length;
      if (!groups[length]) {
        groups[length] = [];
      }
      groups[length].push(name);
    });

    return Object.entries(groups).map(([length, names]) => ({
      length: parseInt(length),
      names,
    }));
  };

  const [selectedNames, setSelectedNames] = useState<string[]>([]);

  const [currentNameLength, setCurrentNameLength] = useState('');

  const { isOpen, onOpen, onClose } = useDisclosure();

  const handleCardClick = (names, length) => {
    setCurrentNameLength(`${length}`);
    onOpen();
    setSelectedNames(names);
  };

  const handleDrawerClose = () => {
    setCurrentNameLength(``);
    onClose();
    setSelectedNames([]);
  };

  const nameGroups: NameGroup[] = groupNamesByLength();

  return (
    <div className="grid grid-cols-6 gap-2">
      {nameGroups.map(({ length, names }) => (
        <NameCard
          key={length}
          length={length}
          names={names}
          onClick={() => handleCardClick(names, length)}
        />
      ))}
      {selectedNames.length > 0 && (
        <Drawer
          onClose={onClose}
          isOpen={isOpen}
          size={'xl'}
          colorScheme={'linkedin'}
        >
          <DrawerOverlay />
          <DrawerContent>
            <DrawerCloseButton />
            <DrawerHeader>Names of length : {currentNameLength}</DrawerHeader>
            <DrawerBody>
              <div style={{ height: '100vh' }}>
                {selectedNames.map((name, index) => (
                  <div
                    style={{ backgroundColor: `${cardColor}`, padding: '24px' }}
                    className={`p-6 bg-${cardColor} rounded-lg cursor-pointer`}
                  >
                    <h2 className="text-2xl font-bold text-white mb-2">
                      {length}
                    </h2>
                  </div>
                ))}
              </div>
            </DrawerBody>
            <DrawerFooter>
              <Button variant="outline" onClick={onClose}>
                Cancel
              </Button>
            </DrawerFooter>
          </DrawerContent>
        </Drawer>
      )}
    </div>
  );
};

export default App;
